package tutorial;

public class Variableanddatatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//ini data primitif
int int_itu_angka = 5;
double double_itu_angka_berkoma = 1.1;
boolean boolean_itu_true_or_false = true;
char char_itu_1_huruf = 'h';

//ngerubah data int diatas
int narik_data_int = int_itu_angka;

//ini data non primit
String string_itu_banyak_huruf = "string";

//ini tarik data
System.out.println(int_itu_angka);
System.out.println(double_itu_angka_berkoma);
System.out.println(boolean_itu_true_or_false);
System.out.println(char_itu_1_huruf);
System.out.println(string_itu_banyak_huruf);

//tarik data yang di ubah
System.out.println(narik_data_int);

	}

}
